import React, { useState, useEffect, useCallback } from 'react';
import { Level, GameState, GamePage } from './types/GameTypes';
import { calculateStarRating } from './utils/GameLogic';
import MainMenu from './components/MainMenu';
import RulesPage from './components/RulesPage';
import NewLevelSelector from './components/NewLevelSelector';
import NewGame from './components/NewGame';
import CompletionEffects from './components/CompletionEffects';
import FinalCelebration from './components/FinalCelebration';
import AudioManager from './utils/AudioManager';
import './App.css';

// Â∞éÂÖ•ÈóúÂç°Ë≥áÊñô
import levelsData from './data/levels.json';

const App: React.FC = () => {
  const [currentPage, setCurrentPage] = useState<GamePage>('main-menu');
  const [currentLevel, setCurrentLevel] = useState<Level | null>(null);
  const [completedLevels, setCompletedLevels] = useState<Set<string>>(new Set());
  const [levelScores, setLevelScores] = useState<Record<string, { stars: number; moves: number }>>({});

  // ÁâπÊïàÁãÄÊÖã
  const [showCompletionEffects, setShowCompletionEffects] = useState(false);
  const [showFinalCelebration, setShowFinalCelebration] = useState(false);
  const [completionStars, setCompletionStars] = useState(0);

  const audioManager = AudioManager.getInstance();

  // Âæû localStorage ËºâÂÖ•ÈÅäÊà≤ÈÄ≤Â∫¶
  useEffect(() => {
    const savedCompleted = localStorage.getItem('completedLevels');
    const savedScores = localStorage.getItem('levelScores');

    if (savedCompleted) {
      try {
        setCompletedLevels(new Set(JSON.parse(savedCompleted)));
      } catch (error) {
        console.error('Error loading completed levels:', error);
      }
    }

    if (savedScores) {
      try {
        setLevelScores(JSON.parse(savedScores));
      } catch (error) {
        console.error('Error loading level scores:', error);
      }
    }
  }, []);

  // ‰øùÂ≠òÈÅäÊà≤ÈÄ≤Â∫¶Âà∞ localStorage
  useEffect(() => {
    localStorage.setItem('completedLevels', JSON.stringify([...completedLevels]));
  }, [completedLevels]);

  useEffect(() => {
    localStorage.setItem('levelScores', JSON.stringify(levelScores));
  }, [levelScores]);

  const handleStartGame = () => {
    setCurrentPage('level-selector');
  };

  const handleShowRules = () => {
    setCurrentPage('rules');
  };

  const handleBackToMainMenu = () => {
    setCurrentPage('main-menu');
    setCurrentLevel(null);
  };

  const handleLevelSelect = (level: Level) => {
    // ËΩâÊèõÊï∏ÁµÑÊ†ºÂºèÂà∞Â∞çË±°Ê†ºÂºè
    const convertedLevel: Level = {
      ...level,
      obstacles: level.obstacles.map((obs: any) => ({ row: obs[0], col: obs[1] })),
      blocks: level.blocks.map((block: any) => ({ row: block[0], col: block[1] }))
    };
    setCurrentLevel(convertedLevel);
    setCurrentPage('game');
  };

  const handleBackToLevelSelector = () => {
    setCurrentPage('level-selector');
    setCurrentLevel(null);
  };

  const handleResetProgress = () => {
    setCompletedLevels(new Set());
    setLevelScores({});
    localStorage.removeItem('completedLevels');
    localStorage.removeItem('levelScores');
  };

  const handleCompletionEffectsComplete = () => {
    setShowCompletionEffects(false);

    // ÊâæÂà∞‰∏ã‰∏ÄÈóú
    const levels = levelsData as unknown as Level[];
    const currentIndex = levels.findIndex(level => currentLevel && level.id === currentLevel.id);
    const hasNextLevel = currentIndex >= 0 && currentIndex < levels.length - 1;
    const nextLevel = hasNextLevel ? levels[currentIndex + 1] : null;

    if (hasNextLevel && nextLevel) {
      const userChoice = window.confirm(`üéâ ÊÅ≠ÂñúÈÄöÈóúÔºÅ\n\nÈªûÊìä„ÄåÁ¢∫ÂÆö„ÄçÈÄ≤ÂÖ•‰∏ã‰∏ÄÈóú\nÈªûÊìä„ÄåÂèñÊ∂à„ÄçËøîÂõûÈóúÂç°ÈÅ∏Êìá`);
      if (userChoice) {
        // ÈÄ≤ÂÖ•‰∏ã‰∏ÄÈóú
        const convertedLevel: Level = {
          ...nextLevel,
          obstacles: nextLevel.obstacles.map((obs: any) => ({ row: obs[0], col: obs[1] })),
          blocks: nextLevel.blocks.map((block: any) => ({ row: block[0], col: block[1] }))
        };
        setCurrentLevel(convertedLevel);
      } else {
        setCurrentPage('level-selector');
        setCurrentLevel(null);
      }
    } else {
      // Â¶ÇÊûúÊ≤íÊúâ‰∏ã‰∏ÄÈóú‰ΩÜÈÇÑÊ≤íÂÆåÊàêÊâÄÊúâÈóúÂç°
      if (completedLevels.size < levels.length) {
        setCurrentPage('level-selector');
        setCurrentLevel(null);
      }
      // Â¶ÇÊûúÂÆåÊàêÊâÄÊúâÈóúÂç°ÔºåÁ≠âÂæÖÊúÄÁµÇÊÖ∂Á•ùÁâπÊïà
    }
  };

  const handleFinalCelebrationComplete = () => {
    setShowFinalCelebration(false);
    setCurrentPage('level-selector');
    setCurrentLevel(null);
  };

  const handleLevelComplete = useCallback((gameState: GameState) => {
    const levelId = gameState.currentLevel.id;
    const movesUsed = gameState.currentTurn - 1;
    const starRating = calculateStarRating(gameState);

    // Êí≠ÊîæÈü≥Êïà
    audioManager.playSound(`star${starRating.stars}`);

    // Êõ¥Êñ∞ÂÆåÊàêÁöÑÈóúÂç°
    const newCompletedLevels = new Set([...completedLevels, levelId]);
    setCompletedLevels(newCompletedLevels);

    // Êõ¥Êñ∞ÂàÜÊï∏ÔºàÂè™‰øùÂ≠òÊõ¥Â•ΩÁöÑÂàÜÊï∏Ôºâ
    setLevelScores(prev => {
      const currentScore = prev[levelId];
      if (!currentScore || starRating.stars > currentScore.stars || 
          (starRating.stars === currentScore.stars && movesUsed < currentScore.moves)) {
        return {
          ...prev,
          [levelId]: {
            stars: starRating.stars,
            moves: movesUsed
          }
        };
      }
      return prev;
    });

    // È°ØÁ§∫ÈÄöÈóúÁâπÊïà
    setCompletionStars(starRating.stars);
    setShowCompletionEffects(true);

    // Ê™¢Êü•ÊòØÂê¶ÂÆåÊàêÊâÄÊúâÈóúÂç°
    const levels = levelsData as unknown as Level[];
    if (newCompletedLevels.size === levels.length) {
      setTimeout(() => {
        setShowFinalCelebration(true);
        audioManager.playSound('finalCelebration');
      }, 3500); // Âú®ÈÄöÈóúÁâπÊïàÁµêÊùüÂæåÈ°ØÁ§∫
    }
  }, [completedLevels, audioManager]);

  const handleLevelFailed = useCallback((gameState: GameState) => {
    const coverage = Math.round((gameState.coveredCells.size / (gameState.currentLevel.gridSize[0] * gameState.currentLevel.gridSize[1] - gameState.currentLevel.obstacles.length)) * 100);

    setTimeout(() => {
      alert(`üòî ÈÅäÊà≤Â§±ÊïóÔºÅ\nË¶ÜËìãÁéá: ${coverage}%\nÂÜçË©¶‰∏ÄÊ¨°ÂêßÔºÅ`);
      setCurrentPage('level-selector');
      setCurrentLevel(null);
    }, 1000);
  }, []);

  const renderCurrentPage = () => {
    switch (currentPage) {
      case 'main-menu':
        return (
          <MainMenu
            onStartGame={handleStartGame}
            onShowRules={handleShowRules}
          />
        );

      case 'rules':
        return (
          <RulesPage
            onBack={handleBackToMainMenu}
          />
        );

      case 'level-selector':
        return (
          <NewLevelSelector
            levels={levelsData as unknown as Level[]}
            completedLevels={completedLevels}
            levelScores={levelScores}
            onLevelSelect={handleLevelSelect}
            onResetProgress={handleResetProgress}
            onBack={handleBackToMainMenu}
          />
        );

      case 'game':
        return currentLevel ? (
          <NewGame
            level={currentLevel}
            onLevelComplete={handleLevelComplete}
            onLevelFailed={handleLevelFailed}
            onBack={handleBackToLevelSelector}
          />
        ) : (
          <div className="loading">ËºâÂÖ•‰∏≠...</div>
        );

      default:
        return (
          <MainMenu
            onStartGame={handleStartGame}
            onShowRules={handleShowRules}
          />
        );
    }
  };

  const totalStars = Object.values(levelScores).reduce((sum, score) => sum + score.stars, 0);
  const maxStars = (levelsData as unknown as Level[]).length * 3;

  return (
    <div className="app">
      {renderCurrentPage()}

      {/* ÈÄöÈóúÁâπÊïà */}
      <CompletionEffects
        stars={completionStars}
        isVisible={showCompletionEffects}
        onComplete={handleCompletionEffectsComplete}
      />

      {/* ÊúÄÁµÇÊÖ∂Á•ùÁâπÊïà */}
      <FinalCelebration
        totalStars={totalStars}
        maxStars={maxStars}
        isVisible={showFinalCelebration}
        onComplete={handleFinalCelebrationComplete}
      />
    </div>
  );
};

export default App;
