// 音效管理系統
class AudioManager {
  private static instance: AudioManager;
  private backgroundMusic: HTMLAudioElement | null = null;
  private soundEffects: Map<string, HTMLAudioElement> = new Map();
  private isEnabled: boolean = true;
  private musicVolume: number = 0.3;
  private effectVolume: number = 0.5;

  private constructor() {
    this.initializeAudio();
  }

  static getInstance(): AudioManager {
    if (!AudioManager.instance) {
      AudioManager.instance = new AudioManager();
    }
    return AudioManager.instance;
  }

  private initializeAudio() {
    // 由於我們不能使用真實的音頻文件，我們將創建音頻上下文來生成音效
    this.createSyntheticBackgroundMusic();
    this.createSoundEffects();
  }

  private createSyntheticBackgroundMusic() {
    // 創建一個循環的背景音樂音頻上下文
    try {
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      this.createMelody(audioContext);
    } catch (error) {
      console.log('Web Audio API not supported');
    }
  }

  private createMelody(audioContext: AudioContext) {
    // 創建輕鬆愉快的旋律
    const notes = [
      { freq: 523.25, duration: 0.5 }, // C5
      { freq: 587.33, duration: 0.5 }, // D5
      { freq: 659.25, duration: 0.5 }, // E5
      { freq: 698.46, duration: 0.5 }, // F5
      { freq: 783.99, duration: 0.5 }, // G5
      { freq: 659.25, duration: 0.5 }, // E5
      { freq: 523.25, duration: 1.0 }, // C5
    ];

    let currentTime = audioContext.currentTime;

    const playMelody = () => {
      if (!this.isEnabled) return;

      notes.forEach((note, index) => {
        const oscillator = audioContext.createOscillator();
        const gainNode = audioContext.createGain();

        oscillator.connect(gainNode);
        gainNode.connect(audioContext.destination);

        oscillator.frequency.setValueAtTime(note.freq, currentTime + index * note.duration);
        oscillator.type = 'sine';

        gainNode.gain.setValueAtTime(0, currentTime + index * note.duration);
        gainNode.gain.linearRampToValueAtTime(this.musicVolume * 0.1, currentTime + index * note.duration + 0.1);
        gainNode.gain.exponentialRampToValueAtTime(0.001, currentTime + index * note.duration + note.duration);

        oscillator.start(currentTime + index * note.duration);
        oscillator.stop(currentTime + index * note.duration + note.duration);
      });

      currentTime += notes.reduce((total, note) => total + note.duration, 0) + 2;
      setTimeout(playMelody, (notes.reduce((total, note) => total + note.duration, 0) + 2) * 1000);
    };

    if (audioContext.state === 'suspended') {
      audioContext.resume().then(() => {
        playMelody();
      });
    } else {
      playMelody();
    }
  }

  private createSoundEffects() {
    // 創建各種音效
    this.createSoundEffect('move', [200, 300, 400], 0.1);
    this.createSoundEffect('complete', [523.25, 659.25, 783.99], 0.3);
    this.createSoundEffect('star1', [523.25], 0.2);
    this.createSoundEffect('star2', [523.25, 659.25], 0.2);
    this.createSoundEffect('star3', [523.25, 659.25, 783.99], 0.2);
    this.createSoundEffect('finalCelebration', [523.25, 659.25, 783.99, 1046.5], 0.5);
  }

  private createSoundEffect(name: string, frequencies: number[], duration: number) {
    try {
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();

      const playEffect = () => {
        if (!this.isEnabled) return;

        frequencies.forEach((freq, index) => {
          const oscillator = audioContext.createOscillator();
          const gainNode = audioContext.createGain();

          oscillator.connect(gainNode);
          gainNode.connect(audioContext.destination);

          oscillator.frequency.setValueAtTime(freq, audioContext.currentTime + index * 0.1);
          oscillator.type = 'triangle';

          gainNode.gain.setValueAtTime(0, audioContext.currentTime + index * 0.1);
          gainNode.gain.linearRampToValueAtTime(this.effectVolume * 0.2, audioContext.currentTime + index * 0.1 + 0.01);
          gainNode.gain.exponentialRampToValueAtTime(0.001, audioContext.currentTime + index * 0.1 + duration);

          oscillator.start(audioContext.currentTime + index * 0.1);
          oscillator.stop(audioContext.currentTime + index * 0.1 + duration);
        });
      };

      this.soundEffects.set(name, { play: playEffect } as any);
    } catch (error) {
      console.log('Could not create sound effect:', name);
    }
  }

  // 公共方法
  setEnabled(enabled: boolean) {
    this.isEnabled = enabled;
    localStorage.setItem('audioEnabled', enabled.toString());
  }

  isAudioEnabled(): boolean {
    const saved = localStorage.getItem('audioEnabled');
    if (saved !== null) {
      this.isEnabled = saved === 'true';
    }
    return this.isEnabled;
  }

  playBackgroundMusic() {
    if (this.isEnabled) {
      // 背景音樂已經在循環播放
    }
  }

  stopBackgroundMusic() {
    // 音效會自動停止
  }

  playSound(soundName: string) {
    if (this.isEnabled && this.soundEffects.has(soundName)) {
      const sound = this.soundEffects.get(soundName);
      if (sound && sound.play) {
        try {
          sound.play();
        } catch (error) {
          console.log('Could not play sound:', soundName);
        }
      }
    }
  }

  setMusicVolume(volume: number) {
    this.musicVolume = Math.max(0, Math.min(1, volume));
  }

  setEffectVolume(volume: number) {
    this.effectVolume = Math.max(0, Math.min(1, volume));
  }
}

export default AudioManager;
