import React, { useEffect, useState } from 'react';
import './FinalCelebration.css';

interface FinalCelebrationProps {
  totalStars: number;
  maxStars: number;
  isVisible: boolean;
  onComplete: () => void;
}

interface Firework {
  id: number;
  x: number;
  y: number;
  particles: FireworkParticle[];
  color: string;
}

interface FireworkParticle {
  x: number;
  y: number;
  vx: number;
  vy: number;
  life: number;
  maxLife: number;
}

const FinalCelebration: React.FC<FinalCelebrationProps> = ({ 
  totalStars, 
  maxStars, 
  isVisible, 
  onComplete 
}) => {
  const [fireworks, setFireworks] = useState<Firework[]>([]);
  const [showText, setShowText] = useState(false);
  const [celebrationLevel, setCelebrationLevel] = useState(1);

  useEffect(() => {
    if (isVisible) {
      // 根據星星數量決定慶祝等級
      const percentage = (totalStars / maxStars) * 100;
      let level = 1;
      if (percentage >= 90) level = 3; // 90%以上 - 傳奇
      else if (percentage >= 70) level = 2; // 70%以上 - 大師
      else level = 1; // 其他 - 冒險者

      setCelebrationLevel(level);
      setShowText(true);
      createFireworks(level);

      const timer = setTimeout(() => {
        setShowText(false);
        setFireworks([]);
        onComplete();
      }, 5000);

      return () => clearTimeout(timer);
    }
  }, [isVisible, totalStars, maxStars, onComplete]);

  const createFireworks = (level: number) => {
    const fireworkCount = level === 1 ? 5 : level === 2 ? 10 : 20;
    const colors = level === 1 
      ? ['#FFD700', '#FFA500']
      : level === 2 
      ? ['#FFD700', '#FF6B6B', '#4ECDC4', '#45B7D1']
      : ['#FFD700', '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7', '#DDA0DD', '#FF7675'];

    const newFireworks: Firework[] = [];

    for (let i = 0; i < fireworkCount; i++) {
      setTimeout(() => {
        const firework: Firework = {
          id: Date.now() + i,
          x: Math.random() * window.innerWidth,
          y: Math.random() * (window.innerHeight * 0.6) + window.innerHeight * 0.2,
          particles: [],
          color: colors[Math.floor(Math.random() * colors.length)]
        };

        // 創建煙花粒子
        for (let j = 0; j < (level * 15); j++) {
          const angle = (Math.PI * 2 * j) / (level * 15);
          const speed = Math.random() * 5 + 3;
          firework.particles.push({
            x: firework.x,
            y: firework.y,
            vx: Math.cos(angle) * speed,
            vy: Math.sin(angle) * speed,
            life: 1,
            maxLife: 1
          });
        }

        setFireworks(prev => [...prev, firework]);
        animateFirework(firework);
      }, i * (level === 1 ? 800 : level === 2 ? 400 : 200));
    }
  };

  const animateFirework = (firework: Firework) => {
    let animationId: number;
    let currentParticles = [...firework.particles];

    const animate = () => {
      currentParticles = currentParticles.map(particle => ({
        ...particle,
        x: particle.x + particle.vx,
        y: particle.y + particle.vy,
        vy: particle.vy + 0.1, // 重力
        vx: particle.vx * 0.98, // 空氣阻力
        life: particle.life - 0.015
      })).filter(particle => particle.life > 0);

      setFireworks(prev => 
        prev.map(fw => 
          fw.id === firework.id 
            ? { ...fw, particles: [...currentParticles] }
            : fw
        )
      );

      if (currentParticles.length > 0) {
        animationId = requestAnimationFrame(animate);
      } else {
        // 移除已完成的煙花
        setFireworks(prev => prev.filter(fw => fw.id !== firework.id));
      }
    };

    animationId = requestAnimationFrame(animate);
  };

  const getCelebrationText = () => {
    const percentage = (totalStars / maxStars) * 100;
    if (percentage >= 90) {
      return {
        title: '傳奇冒險者！',
        subtitle: '完美征服所有挑戰！',
        rank: '★ 傳奇 ★'
      };
    } else if (percentage >= 70) {
      return {
        title: '冒險大師！',
        subtitle: '展現了卓越的技巧！',
        rank: '★ 大師 ★'
      };
    } else {
      return {
        title: '勇敢冒險者！',
        subtitle: '完成了所有關卡！',
        rank: '★ 冒險者 ★'
      };
    }
  };

  if (!isVisible) return null;

  const celebrationText = getCelebrationText();

  return (
    <div className="final-celebration-overlay">
      {/* 煙花效果 */}
      <div className="fireworks-container">
        {fireworks.map(firework => (
          <div key={firework.id}>
            {firework.particles.map((particle, index) => (
              <div
                key={index}
                className="firework-particle"
                style={{
                  left: `${particle.x}px`,
                  top: `${particle.y}px`,
                  backgroundColor: firework.color,
                  opacity: particle.life,
                  boxShadow: `0 0 10px ${firework.color}`
                }}
              />
            ))}
          </div>
        ))}
      </div>

      {/* 慶祝文字 */}
      {showText && (
        <div className={`final-celebration-text level-${celebrationLevel}`}>
          <div className="celebration-rank">{celebrationText.rank}</div>
          <div className="celebration-main-title">{celebrationText.title}</div>
          <div className="celebration-subtitle">{celebrationText.subtitle}</div>
          <div className="celebration-stats">
            <div className="stat-item">
              <span className="stat-number">{totalStars}</span>
              <span className="stat-label">總星數</span>
            </div>
            <div className="stat-divider">／</div>
            <div className="stat-item">
              <span className="stat-number">{maxStars}</span>
              <span className="stat-label">滿星</span>
            </div>
          </div>
          <div className="celebration-percentage">
            {Math.round((totalStars / maxStars) * 100)}% 完成度
          </div>
        </div>
      )}

      {/* 背景光環效果 */}
      <div className={`celebration-aura aura-${celebrationLevel}`}></div>

      {/* 旋轉光環 */}
      <div className={`rotating-rings level-${celebrationLevel}`}>
        <div className="ring ring-1"></div>
        <div className="ring ring-2"></div>
        <div className="ring ring-3"></div>
      </div>
    </div>
  );
};

export default FinalCelebration;
