import React from 'react';
import { Direction } from '../types/GameTypes';
import './GameControls.css';

interface GameControlsProps {
  onMove: (direction: Direction) => void;
  onUndo: () => void;
  onReset: () => void;
  onHint: () => void;
  canUndo: boolean;
  isGameActive: boolean;
}

const GameControls: React.FC<GameControlsProps> = ({
  onMove,
  onUndo,
  onReset,
  onHint,
  canUndo,
  isGameActive
}) => {
  const handleKeyPress = (event: React.KeyboardEvent) => {
    if (!isGameActive) return;

    switch (event.key) {
      case 'ArrowUp':
      case 'w':
      case 'W':
        event.preventDefault();
        onMove('up');
        break;
      case 'ArrowDown':
      case 's':
      case 'S':
        event.preventDefault();
        onMove('down');
        break;
      case 'ArrowLeft':
      case 'a':
      case 'A':
        event.preventDefault();
        onMove('left');
        break;
      case 'ArrowRight':
      case 'd':
      case 'D':
        event.preventDefault();
        onMove('right');
        break;
    }
  };

  return (
    <div className="game-controls" onKeyDown={handleKeyPress} tabIndex={0}>
      <div className="control-buttons">
        <button
          className="control-btn hint-btn"
          onClick={onHint}
          disabled={!isGameActive}
          title="ÊèêÁ§∫ (H)"
        >
          üí° ÊèêÁ§∫
        </button>

        <button
          className="control-btn undo-btn"
          onClick={onUndo}
          disabled={!canUndo || !isGameActive}
          title="Êí§Èä∑ (Z)"
        >
          ‚Ü∂ Êí§Èä∑
        </button>

        <button
          className="control-btn reset-btn"
          onClick={onReset}
          disabled={!isGameActive}
          title="ÈáçÊñ∞ÈñãÂßã (R)"
        >
          üîÑ ÈáçÊñ∞ÈñãÂßã
        </button>
      </div>

      <div className="direction-controls">
        <div className="direction-row">
          <button
            className="direction-btn up-btn"
            onClick={() => onMove('up')}
            disabled={!isGameActive}
            title="Âêë‰∏ä (‚Üë Êàñ W)"
          >
            ‚Üë
          </button>
        </div>

        <div className="direction-row">
          <button
            className="direction-btn left-btn"
            onClick={() => onMove('left')}
            disabled={!isGameActive}
            title="ÂêëÂ∑¶ (‚Üê Êàñ A)"
          >
            ‚Üê
          </button>

          <div className="center-space"></div>

          <button
            className="direction-btn right-btn"
            onClick={() => onMove('right')}
            disabled={!isGameActive}
            title="ÂêëÂè≥ (‚Üí Êàñ D)"
          >
            ‚Üí
          </button>
        </div>

        <div className="direction-row">
          <button
            className="direction-btn down-btn"
            onClick={() => onMove('down')}
            disabled={!isGameActive}
            title="Âêë‰∏ã (‚Üì Êàñ S)"
          >
            ‚Üì
          </button>
        </div>
      </div>

      <div className="control-instructions">
        <p>‰ΩøÁî®ÊñπÂêëÈçµ„ÄÅWASD ÊàñÈªûÊìäÊåâÈàïÁßªÂãï</p>
        <p>ÊâÄÊúâÊñπÂ°äÊúÉÂêåÊôÇÊúùÊåáÂÆöÊñπÂêëÊªëÂãï</p>
      </div>
    </div>
  );
};

export default GameControls;
