import React, { useState, useEffect } from 'react';
import './MainMenu.css';
import AudioManager from '../utils/AudioManager';

interface MainMenuProps {
  onStartGame: () => void;
  onShowRules: () => void;
}

const MainMenu: React.FC<MainMenuProps> = ({ onStartGame, onShowRules }) => {
  const [isAudioEnabled, setIsAudioEnabled] = useState(true);
  const audioManager = AudioManager.getInstance();

  useEffect(() => {
    setIsAudioEnabled(audioManager.isAudioEnabled());
    if (audioManager.isAudioEnabled()) {
      audioManager.playBackgroundMusic();
    }
  }, [audioManager]);

  const toggleAudio = () => {
    const newState = !isAudioEnabled;
    setIsAudioEnabled(newState);
    audioManager.setEnabled(newState);
    if (newState) {
      audioManager.playBackgroundMusic();
    } else {
      audioManager.stopBackgroundMusic();
    }
  };

  return (
    <div className="main-menu-container">
      <div className="main-menu-content">
        {/* 讓底圖的原始標題顯示，不添加自定義文字 */}

        {/* 音樂開關 */}
        <div className="audio-toggle-container">
          <button 
            className={`audio-toggle ${isAudioEnabled ? 'enabled' : 'disabled'}`}
            onClick={toggleAudio}
            title={isAudioEnabled ? '關閉音樂' : '開啟音樂'}
          >
            <div className="audio-icon">
              {isAudioEnabled ? (
                <div className="sound-waves">
                  <span className="wave wave-1"></span>
                  <span className="wave wave-2"></span>
                  <span className="wave wave-3"></span>
                </div>
              ) : (
                <div className="sound-off">
                  <span className="mute-line"></span>
                </div>
              )}
            </div>
          </button>
        </div>

        <div className="menu-buttons">
          <button 
            className="menu-button start-button"
            onClick={onStartGame}
          >
            開始遊戲
          </button>

          <button 
            className="menu-button rules-button"
            onClick={onShowRules}
          >
            遊戲規則
          </button>
        </div>
      </div>
    </div>
  );
};

export default MainMenu;
