import React from 'react';
import Icon from './Icon';
import './MainMenu.css';

interface MainMenuProps {
  onStartGame: () => void;
  onShowRules: () => void;
}

const MainMenu: React.FC<MainMenuProps> = ({ onStartGame, onShowRules }) => {
  return (
    <div className="main-menu-container">
      {/* Background Overlay */}
      <div className="main-menu-overlay" />

      {/* Content */}
      <div className="main-menu-content">
        {/* Game Title */}
        <div className="game-title-section">
          <h1 className="game-title">滑動方塊覆蓋遊戲</h1>
          <p className="game-subtitle">Sliding Blocks Coverage Game</p>
          <div className="game-description">
            <p>控制多個方塊，讓所有格子都被覆蓋</p>
            <p>挑戰你的邏輯思維和策略規劃能力</p>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="menu-actions">
          <button 
            className="btn btn-primary btn-xl menu-button start-button" 
            onClick={onStartGame}
            aria-label="開始遊戲"
          >
            <Icon name="play" size="lg" />
            開始遊戲
          </button>

          <button 
            className="btn btn-secondary btn-xl menu-button rules-button" 
            onClick={onShowRules}
            aria-label="查看遊戲規則"
          >
            <Icon name="rules" size="lg" />
            遊戲規則
          </button>
        </div>

        {/* Game Features */}
        <div className="game-features">
          <div className="feature-item">
            <Icon name="target" size="md" color="var(--color-accent)" />
            <span>8個精心設計的關卡</span>
          </div>
          <div className="feature-item">
            <Icon name="star" size="md" color="var(--color-warning)" />
            <span>三星評分系統</span>
          </div>
          <div className="feature-item">
            <Icon name="trophy" size="md" color="var(--color-success)" />
            <span>挑戰完美通關</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MainMenu;
