import React from 'react';
import './ProgressBar.css';

interface ProgressBarProps {
  current: number;
  total: number;
  label?: string;
  showPercentage?: boolean;
  animated?: boolean;
  color?: 'primary' | 'success' | 'warning' | 'accent';
  size?: 'sm' | 'md' | 'lg';
}

const ProgressBar: React.FC<ProgressBarProps> = ({
  current,
  total,
  label,
  showPercentage = true,
  animated = true,
  color = 'primary',
  size = 'md'
}) => {
  const percentage = total > 0 ? Math.round((current / total) * 100) : 0;

  const sizeClasses = {
    sm: 'progress-sm',
    md: 'progress-md',
    lg: 'progress-lg'
  };

  const colorClasses = {
    primary: 'progress-primary',
    success: 'progress-success',
    warning: 'progress-warning',
    accent: 'progress-accent'
  };

  return (
    <div className={`progress-container ${sizeClasses[size]}`}>
      {label && (
        <div className="progress-header">
          <span className="progress-label">{label}</span>
          {showPercentage && (
            <span className="progress-percentage">{percentage}%</span>
          )}
        </div>
      )}

      <div className="progress-track">
        <div 
          className={`progress-fill ${colorClasses[color]} ${animated ? 'animated' : ''}`}
          style={{ width: `${percentage}%` }}
          role="progressbar"
          aria-valuenow={current}
          aria-valuemin={0}
          aria-valuemax={total}
          aria-label={`${label || 'Progress'}: ${current} of ${total} (${percentage}%)`}
        >
          <div className="progress-glow" />
        </div>
      </div>

      <div className="progress-stats">
        <span className="progress-current">{current}</span>
        <span className="progress-separator">/</span>
        <span className="progress-total">{total}</span>
      </div>
    </div>
  );
};

export default ProgressBar;
