import React from 'react';
import './Icon.css';

export interface IconProps {
  name: 'target' | 'gamepad' | 'document' | 'crown' | 'star' | 'moves' | 'goal' | 'scoring' | 'controls' | 'rules' | 'arrow-up' | 'arrow-down' | 'arrow-left' | 'arrow-right' | 'undo' | 'reset' | 'hint' | 'audio-on' | 'audio-off' | 'back' | 'next' | 'previous' | 'play' | 'pause' | 'settings' | 'trophy' | 'medal' | 'check' | 'cross' | 'info' | 'warning' | 'error' | 'success';
  size?: 'sm' | 'md' | 'lg' | 'xl';
  className?: string;
  color?: string;
}

const Icon: React.FC<IconProps> = ({ 
  name, 
  size = 'md', 
  className = '', 
  color 
}) => {
  const sizeClasses = {
    sm: 'icon-sm',
    md: 'icon-md', 
    lg: 'icon-lg',
    xl: 'icon-xl'
  };

  return (
    <span 
      className={`icon icon-${name} ${sizeClasses[size]} ${className}`}
      style={{ color }}
      role="img"
      aria-label={getIconLabel(name)}
    />
  );
};

const getIconLabel = (name: string): string => {
  const labels: Record<string, string> = {
    'target': 'Target',
    'gamepad': 'Gamepad',
    'document': 'Document',
    'crown': 'Crown',
    'star': 'Star',
    'moves': 'Moves',
    'goal': 'Goal',
    'scoring': 'Scoring',
    'controls': 'Controls',
    'rules': 'Rules',
    'arrow-up': 'Up arrow',
    'arrow-down': 'Down arrow',
    'arrow-left': 'Left arrow',
    'arrow-right': 'Right arrow',
    'undo': 'Undo',
    'reset': 'Reset',
    'hint': 'Hint',
    'audio-on': 'Audio on',
    'audio-off': 'Audio off',
    'back': 'Back',
    'next': 'Next',
    'previous': 'Previous',
    'play': 'Play',
    'pause': 'Pause',
    'settings': 'Settings',
    'trophy': 'Trophy',
    'medal': 'Medal',
    'check': 'Check',
    'cross': 'Cross',
    'info': 'Information',
    'warning': 'Warning',
    'error': 'Error',
    'success': 'Success'
  };

  return labels[name] || name;
};

export default Icon;
