import React from 'react';
import { GameState } from '../types/GameTypes';
import { calculateCoverage, calculateStarRating } from '../utils/GameLogic';
import './GameInfo.css';

interface GameInfoProps {
  gameState: GameState;
}

const GameInfo: React.FC<GameInfoProps> = ({ gameState }) => {
  const { currentLevel, currentTurn, isGameWon, isGameLost } = gameState;
  const coverage = calculateCoverage(gameState);
  const starRating = calculateStarRating(gameState);

  const getDifficultyColor = (difficulty: string) => {
    switch (difficulty) {
      case 'Easy': return '#4caf50';
      case 'Normal': return '#ff9800';
      case 'Hard': return '#f44336';
      case 'Expert': return '#9c27b0';
      default: return '#666';
    }
  };

  const getStatusMessage = () => {
    if (isGameWon) {
      return (
        <div className="status-message success">
          <h3>üéâ ÊÅ≠ÂñúÂÆåÊàêÔºÅ</h3>
          <p>Ë¶ÜËìãÁéá: {coverage}%</p>
          <p>Ë©ïÂàÜ: {starRating.stars}‚òÖ {starRating.description}</p>
        </div>
      );
    }

    if (isGameLost) {
      return (
        <div className="status-message failure">
          <h3>üòî ÈÅäÊà≤ÁµêÊùü</h3>
          <p>ÂõûÂêàÊï∏Â∑≤Áî®ÂÆå</p>
          <p>Ë¶ÜËìãÁéá: {coverage}%</p>
        </div>
      );
    }

    return (
      <div className="status-message playing">
        <h3>üéÆ ÈÅäÊà≤ÈÄ≤Ë°å‰∏≠</h3>
        <p>ÁπºÁ∫åÁßªÂãïÊñπÂ°äË¶ÜËìãÊâÄÊúâÊ†ºÂ≠ê</p>
      </div>
    );
  };

  return (
    <div className="game-info">
      <div className="level-info">
        <h2 className="level-title">
          {currentLevel.id} - {currentLevel.difficulty}
        </h2>
        <div 
          className="difficulty-badge"
          style={{ backgroundColor: getDifficultyColor(currentLevel.difficulty) }}
        >
          {currentLevel.difficulty}
        </div>
      </div>

      <div className="progress-info">
        <div className="progress-item">
          <span className="progress-label">ÂõûÂêàÊï∏:</span>
          <span className="progress-value">
            {currentTurn - 1} / {currentLevel.turnLimit}
          </span>
        </div>

        <div className="progress-item">
          <span className="progress-label">Ë¶ÜËìãÁéá:</span>
          <span className="progress-value">{coverage}%</span>
        </div>

        <div className="progress-item">
          <span className="progress-label">ÊúÄÂ∞ëÊ≠•Êï∏:</span>
          <span className="progress-value">{currentLevel.minSteps}</span>
        </div>
      </div>

      <div className="coverage-bar">
        <div className="coverage-label">Ë¶ÜËìãÈÄ≤Â∫¶</div>
        <div className="coverage-track">
          <div 
            className="coverage-fill"
            style={{ width: `${coverage}%` }}
          ></div>
        </div>
        <div className="coverage-percentage">{coverage}%</div>
      </div>

      <div className="level-stats">
        <div className="stat-item">
          <span className="stat-label">Âú∞ÂúñÂ§ßÂ∞è:</span>
          <span className="stat-value">{currentLevel.gridSize[0]}√ó{currentLevel.gridSize[1]}</span>
        </div>

        <div className="stat-item">
          <span className="stat-label">ÊñπÂ°äÊï∏Èáè:</span>
          <span className="stat-value">{currentLevel.blocks.length}</span>
        </div>

        <div className="stat-item">
          <span className="stat-label">ÈöúÁ§ôÁâ©:</span>
          <span className="stat-value">{currentLevel.obstacles.length}</span>
        </div>
      </div>

      {getStatusMessage()}
    </div>
  );
};

export default GameInfo;
