import React, { useEffect, useState } from 'react';
import './CompletionEffects.css';

interface CompletionEffectsProps {
  stars: number;
  isVisible: boolean;
  onComplete: () => void;
}

interface Particle {
  id: number;
  x: number;
  y: number;
  vx: number;
  vy: number;
  color: string;
  size: number;
  life: number;
  maxLife: number;
}

const CompletionEffects: React.FC<CompletionEffectsProps> = ({ stars, isVisible, onComplete }) => {
  const [particles, setParticles] = useState<Particle[]>([]);
  const [showText, setShowText] = useState(false);

  useEffect(() => {
    if (isVisible) {
      setShowText(true);
      createParticles(stars);

      const timer = setTimeout(() => {
        setShowText(false);
        setParticles([]);
        onComplete();
      }, 3000);

      return () => clearTimeout(timer);
    }
  }, [isVisible, stars, onComplete]);

  const createParticles = (starCount: number) => {
    const newParticles: Particle[] = [];
    const particleCount = starCount === 1 ? 30 : starCount === 2 ? 60 : 100;

    for (let i = 0; i < particleCount; i++) {
      const colors = starCount === 1 
        ? ['#FFD700', '#FFA500', '#FF8C00']
        : starCount === 2 
        ? ['#FFD700', '#FF6B6B', '#4ECDC4', '#45B7D1']
        : ['#FFD700', '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7', '#DDA0DD'];

      newParticles.push({
        id: i,
        x: Math.random() * window.innerWidth,
        y: Math.random() * window.innerHeight,
        vx: (Math.random() - 0.5) * 10,
        vy: (Math.random() - 0.5) * 10,
        color: colors[Math.floor(Math.random() * colors.length)],
        size: Math.random() * 8 + 4,
        life: 1,
        maxLife: 1
      });
    }

    setParticles(newParticles);
    animateParticles(newParticles);
  };

  const animateParticles = (initialParticles: Particle[]) => {
    let animationId: number;
    let currentParticles = [...initialParticles];

    const animate = () => {
      currentParticles = currentParticles.map(particle => ({
        ...particle,
        x: particle.x + particle.vx,
        y: particle.y + particle.vy,
        vy: particle.vy + 0.2, // 重力效果
        life: particle.life - 0.02
      })).filter(particle => particle.life > 0);

      setParticles([...currentParticles]);

      if (currentParticles.length > 0) {
        animationId = requestAnimationFrame(animate);
      }
    };

    animationId = requestAnimationFrame(animate);

    return () => {
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
    };
  };

  const getCompletionText = () => {
    switch (stars) {
      case 1:
        return '完成！';
      case 2:
        return '很棒！';
      case 3:
        return '完美！';
      default:
        return '完成！';
    }
  };

  const getStarDisplay = () => {
    return '★'.repeat(stars) + '☆'.repeat(3 - stars);
  };

  if (!isVisible) return null;

  return (
    <div className="completion-effects-overlay">
      {/* 粒子效果 */}
      <div className="particles-container">
        {particles.map(particle => (
          <div
            key={particle.id}
            className="particle"
            style={{
              left: `${particle.x}px`,
              top: `${particle.y}px`,
              backgroundColor: particle.color,
              width: `${particle.size}px`,
              height: `${particle.size}px`,
              opacity: particle.life,
              boxShadow: `0 0 ${particle.size * 2}px ${particle.color}`
            }}
          />
        ))}
      </div>

      {/* 完成文字 */}
      {showText && (
        <div className={`completion-text stars-${stars}`}>
          <div className="completion-title">{getCompletionText()}</div>
          <div className="completion-stars">{getStarDisplay()}</div>
          <div className="completion-sparkle">✨</div>
        </div>
      )}

      {/* 背景光效 */}
      <div className={`completion-glow glow-${stars}`}></div>
    </div>
  );
};

export default CompletionEffects;
