import React, { useEffect, useState, useRef } from 'react';
import './VictoryEffect.css';

interface VictoryEffectProps {
  stars: number;
  isAllComplete?: boolean;
  totalStars?: number;
  onComplete: () => void;
}

const VictoryEffect: React.FC<VictoryEffectProps> = ({ 
  stars, 
  isAllComplete = false, 
  totalStars = 0, 
  onComplete 
}) => {
  const [showEffect, setShowEffect] = useState(true);
  const [fadeOut, setFadeOut] = useState(false);
  const [particles, setParticles] = useState<Array<{ id: number; x: number; y: number; color: string; delay: number }>>([]);
  const onCompleteRef = useRef(onComplete);

  // Êõ¥Êñ∞refÁï∂onCompleteÊîπËÆä
  useEffect(() => {
    onCompleteRef.current = onComplete;
  }, [onComplete]);

  useEffect(() => {
    console.log('VictoryEffect: ÁµÑ‰ª∂ÂàùÂßãÂåñÔºåË®≠ÂÆöÂÆöÊôÇÂô®');

    // ÁîüÊàêÁ≤íÂ≠êÊïàÊûú
    const particleCount = isAllComplete ? 50 : stars * 15;
    const newParticles = Array.from({ length: particleCount }, (_, i) => ({
      id: i,
      x: Math.random() * 100,
      y: Math.random() * 100,
      color: getParticleColor(stars, isAllComplete),
      delay: Math.random() * 0.5
    }));
    setParticles(newParticles);

    // Êõ¥Áü≠ÁöÑÊôÇÈñìÊ∏¨Ë©¶
    const timer = setTimeout(() => {
      console.log('VictoryEffect: ÂÆöÊôÇÂô®Ëß∏ÁôºÔºÅ');
      try {
        console.log('VictoryEffect: Ë®≠ÂÆöÊ∑°Âá∫ÁãÄÊÖã');
        setFadeOut(true);
        setShowEffect(false);

        console.log('VictoryEffect: Ê∫ñÂÇôË™øÁî® onComplete');
        console.log('VictoryEffect: onComplete ÂáΩÊï∏Â≠òÂú®Ôºü', typeof onCompleteRef.current === 'function');

        // Ë™øÁî®ÂõûË™ø
        if (onCompleteRef.current) {
          onCompleteRef.current();
          console.log('VictoryEffect: onComplete Ë™øÁî®ÂÆåÊàê');
        }
      } catch (error) {
        console.error('VictoryEffect: ÈåØË™§ÔºÅ', error);
      }
    }, 2000); // Á∏ÆÁü≠Âà∞2ÁßíÊ∏¨Ë©¶

    return () => {
      console.log('VictoryEffect: useEffect Ê∏ÖÁêÜÔºåÊ∏ÖÈô§ÂÆöÊôÇÂô®');
      clearTimeout(timer);
    };
  }, [stars, isAllComplete]); // ÁßªÈô§ onComplete ‰æùË≥¥

  const getParticleColor = (stars: number, isAllComplete: boolean) => {
    if (isAllComplete) {
      const colors = ['#FFD700', '#FF6B6B', '#4ECDC4', '#45B7D1', '#96CEB4', '#FFEAA7'];
      return colors[Math.floor(Math.random() * colors.length)];
    }

    switch (stars) {
      case 1: return '#32CD32'; // Á∂†Ëâ≤
      case 2: return '#FFD700'; // ÈáëËâ≤
      case 3: return '#FF6B6B'; // Á¥ÖËâ≤
      default: return '#87CEEB';
    }
  };

  const getEffectTitle = () => {
    if (isAllComplete) {
      if (totalStars >= 21) return 'ÂÆåÁæéÂ§ßÂ∏´ÔºÅ'; // 7Èóú * 3Êòü
      if (totalStars >= 14) return 'ÂÑ™ÁßÄÂÜíÈö™ÂÆ∂ÔºÅ'; // 7Èóú * 2ÊòüÂπ≥Âùá
      return 'ÂãáÊï¢Êé¢Èö™ËÄÖÔºÅ';
    }

    switch (stars) {
      case 1: return '‰∏çÈåØÁöÑÂòóË©¶ÔºÅ';
      case 2: return 'ÂÅöÂæóÂæàÂ•ΩÔºÅ';
      case 3: return 'ÂÆåÁæéÈÄöÈóúÔºÅ';
      default: return 'ÊÅ≠ÂñúÈÅéÈóúÔºÅ';
    }
  };

  const getEffectSubtitle = () => {
    if (isAllComplete) {
      return `Á∏ΩÂÖ±Áç≤Âæó ${totalStars} È°ÜÊòüÊòüÔºÅ`;
    }

    return `Áç≤Âæó ${stars} È°ÜÊòüÊòüÔºÅ`;
  };

  if (!showEffect) return null;

  return (
    <div className={`victory-effect ${isAllComplete ? 'all-complete' : `stars-${stars}`} ${fadeOut ? 'fade-out' : ''}`}>
      {/* ËÉåÊôØÈÅÆÁΩ© */}
      <div className="victory-backdrop" />

      {/* Á≤íÂ≠êÊïàÊûú */}
      <div className="particles-container">
        {particles.map(particle => (
          <div
            key={particle.id}
            className="particle"
            style={{
              left: `${particle.x}%`,
              top: `${particle.y}%`,
              backgroundColor: particle.color,
              animationDelay: `${particle.delay}s`
            }}
          />
        ))}
      </div>

      {/* ‰∏ªË¶ÅÂÖßÂÆπ */}
      <div className="victory-content">
        {/* ÊòüÊòüÈ°ØÁ§∫ */}
        <div className="stars-display">
          {isAllComplete ? (
            <div className="trophy-icon">üèÜ</div>
          ) : (
            Array.from({ length: 3 }, (_, i) => (
              <div
                key={i}
                className={`star ${i < stars ? 'filled' : 'empty'}`}
                style={{ animationDelay: `${i * 0.2}s` }}
              >
                ‚òÖ
              </div>
            ))
          )}
        </div>

        {/* ÊñáÂ≠óÂÖßÂÆπ */}
        <div className="victory-text">
          <h1 className="victory-title">{getEffectTitle()}</h1>
          <p className="victory-subtitle">{getEffectSubtitle()}</p>
        </div>

        {/* È°çÂ§ñÁöÑË¶ñË¶∫ÊïàÊûú */}
        <div className="victory-rings">
          <div className="ring ring-1" />
          <div className="ring ring-2" />
          <div className="ring ring-3" />
        </div>
      </div>

      {/* ÂΩ©Â∏∂ÊïàÊûúÔºàÂÉÖÂÖ®ÈóúÂç°ÂÆåÊàêÊôÇÈ°ØÁ§∫Ôºâ */}
      {isAllComplete && (
        <div className="confetti-container">
          {Array.from({ length: 20 }, (_, i) => (
            <div
              key={i}
              className="confetti"
              style={{
                left: `${Math.random() * 100}%`,
                animationDelay: `${Math.random() * 2}s`,
                backgroundColor: getParticleColor(0, true)
              }}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default VictoryEffect;
